import React from 'react';

export default class CommentAdd extends React.Component {

    state = {
        nameText: '',
        commentText: ''
    }

    submit = (e) => {
        e.preventDefault()
        this.props.addComment(this.state.comment)
        this.setState({
            nameText:'',
            commentText:''
        })
    }

    change = (e) => {
        const nameText = e.target.value
        this.setState({nameText})
    }

    changeT = (e) => {
        const commentText = e.target.value
        this.setState({commentText})
    }

    render() {
        return (
            <div className="ui segement">
                <form className="ui form">
                    <div className="ui field">
                    user name:
                    <input value={this.state.nameText} onChange={this.change} />
                    user comment:
                    <input value={this.state.commentText} onChange={this.changeT} />
                    <div>
                    <button className="ui primary loading button" onClick={this.submit}>submit</button>
                    </div>
                </div>
                </form>
            </div>
        )
    }
}
