import React from 'react';

export default class CommentAdd extends React.Component {

    state = {
        nameText: '',
        commentText: ''
    }

    render() {
        return (
            <div className="ui segement">
                <form className="ui form">
                    <div className="ui field">
                    user name:
                    <input value={this.state.nameText} />
                    user comment:
                    <input value={this.state.commentText} />
                    <div>
                        <button className="ui active button">
                        <i className="user icon"></i>
                        Submit
                        </button>
                    </div>
                </div>
                </form>
            </div>
        )
    }
}
