    state = {
        comments: [
            {user: 'tom', context: 'react good'},
            {user: 'wahaha', context: 'react bad'}
        ]
}



    render() {
        return (
            <div>
                <div className="ui huge header">
                    Comment your React
                </div>

                {/* <CommentAdd /> */}
                <div>
                    <CommentList comments={this.state.comments} />
                </div>
            </div>
        )
    }
}
